
$('#vmc-slide').vmcSimpleSlide({

  // width/height
  width: 'auto',
  height: 'auto',

  // min width
  minWidth: 0,

  // min height
  minHeight: 0,

  // autoplay
  auto: true,

  // 'right' or 'left'
  autoPlayDirection: 'right',

  // duration of animation
  duration: 4000,

  // animation speed
  speed: 600,

  // shows navigation buttons
  showSideButton: true,

  // shows pagination bullets
  showBottomButton: true,

  // shows image caption
  showSummary: 'auto',

  // allows HTML content
  summaryIsHtml: false,

  // pause on hover
  hoverStop: true
  
});